INSERT INTO `wp_postmeta` VALUES (466,83,'slider_description','Se det rekonstruerede Esbjergs bymiljø, som det må have set ud omkring 1900-1950.\r\n\r\nI den periode udviklede Esbjerg sig til en stor by, der stadig havde nære forbindelser til havnen.\r\n\r\nStå på Torvet, se de eksklusive butikker som guldsmeden fra 1908 og modebutikken fra 1925 og kig ind hos købmanden på hjørnet (1938-1950)\r\n\r\nBesøg Havnen - Esbjergs hjerte, og kig inden for i lejlighederne hos arbejderfamilien og direktøren. ');
