INSERT INTO `wp_postmeta` VALUES (488,85,'slider_description','Hvem var modstandsfolkene i Esbjerg, Ribe og Bramming under besættelsen – hvor kom de fra, hvad ville de, og hvad angreb de?\r\n\r\nFå fortællingen om modstand og sabotage i Sydvestjylland fra den spæde start med drengegrupper og kommunister over Danmarks første bystrejke i Esbjerg august 1943 til jernbanesabotager og likvideringer i krigens slutfase.\r\n\r\nSe forslaget til, hvordan MODSTAND kan danne baggrund for abejde med kildekritiske begreber til analyse af historiske spor og udtryksformer i folkeskolens ældste klasser under MODSTAND i undervisningen\r\n\r\nDer ligger forskning bag\r\n\r\n\"MODSTAND Esbjerg Ribe Bramming 1940-45\" bygger på Historisk Samling fra Besættelsestidens omfattende materiale om - og ny forskning i emnet.');
